import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import ManagerLayout from '../components/ManagerLayout';
import '../styles/InvoiceForm.css'; // We'll create this CSS file
import { FaSave, FaTimesCircle } from 'react-icons/fa';

function ManagerNewInvoice() {
  const navigate = useNavigate();
  const [customerName, setCustomerName] = useState('');
  const [customerPhone, setCustomerPhone] = useState('');
  // Default to today's date in YYYY-MM-DD format for the input type="date"
  const [invoiceDate, setInvoiceDate] = useState(new Date().toISOString().slice(0, 10));
  const [items, setItems] = useState([{ quantity: '', description: '', weight: '', price: '', amount: 0 }]);
  const [totalAmount, setTotalAmount] = useState(0);

  // Calculate Amount for an item
  const calculateAmount = (quantity, price) => {
    const numQuantity = parseFloat(quantity) || 0;
    const numPrice = parseFloat(price) || 0;
    return (numQuantity * numPrice);
  };

  // Update item and recalculate amounts
  const handleItemChange = (index, field, value) => {
    const newItems = [...items];
    newItems[index][field] = value;

    // Recalculate amount for the changed item
    if (field === 'quantity' || field === 'price') {
        newItems[index].amount = calculateAmount(newItems[index].quantity, newItems[index].price);
    }

    setItems(newItems);
  };

  // Add a new empty item row if the last row is being edited
  useEffect(() => {
    const lastItem = items[items.length - 1];
    if (lastItem.quantity || lastItem.description || lastItem.weight || lastItem.price) {
      setItems([...items, { quantity: '', description: '', weight: '', price: '', amount: 0 }]);
    }
     // Calculate total whenever items change
    const total = items.reduce((sum, item) => sum + (item.amount || 0), 0);
    setTotalAmount(total);

  }, [items]); // Dependency array includes items


  const handleSave = () => {
    // TODO: Implement actual save logic - send data to backend
    console.log("Saving Invoice:", { customerName, customerPhone, invoiceDate, items: items.slice(0,-1) }); // Exclude the last empty row
    navigate('/orders'); // Navigate back to active orders after save
  };

  const handleCancel = () => {
    navigate('/orders'); // Navigate back without saving
  };

  return (
    <ManagerLayout>
      <div className="invoice-form-container">
        <div className="invoice-form-header">
          <h2>Create New Invoice</h2>
          <div className="form-actions">
             <button onClick={handleSave} className="form-button save-button">
               <FaSave /> Save
            </button>
             <button onClick={handleCancel} className="form-button cancel-button">
               <FaTimesCircle /> Cancel
             </button>
          </div>
        </div>

        <div className="invoice-sheet">
          {/* Invoice Header Section */}
          <div className="invoice-header-details">
             <div className="header-row">
                <div className="header-item">
                   <label htmlFor="customerName">Name:</label>
                   <input
                     type="text"
                     id="customerName"
                     value={customerName}
                     onChange={(e) => setCustomerName(e.target.value)}
                     placeholder="Customer Name"
                   />
                </div>
                <div className="header-item">
                  <label>Invoice #:</label>
                  <span>{/* Placeholder - Assigned on Save */}</span>
                 </div>
             </div>
            <div className="header-row">
                 <div className="header-item">
                     <label htmlFor="customerPhone">Phone:</label>
                     <input
                         type="tel" // Use type=tel for phone numbers
                         id="customerPhone"
                         value={customerPhone}
                         onChange={(e) => setCustomerPhone(e.target.value)}
                         placeholder="Customer Phone"
                     />
                 </div>
                 <div className="header-item">
                    <label htmlFor="invoiceDate">Date:</label>
                    <input
                      type="date"
                      id="invoiceDate"
                      value={invoiceDate}
                      onChange={(e) => setInvoiceDate(e.target.value)}
                    />
                 </div>
            </div>
          </div>

          {/* Invoice Body - Items Table */}
          <div className="invoice-body scrollable-items">
            <table className="invoice-items-table">
              <thead>
                <tr>
                  {/* Add 'Ordered' column based on image */}
                  <th className="col-ordered">Ordered</th>
                  <th className="col-description">Description</th>
                  <th className="col-date">Date</th> {/* Date per item? Might just use header date */}
                  <th className="col-qty">Qty</th>
                  <th className="col-price">Price</th>
                  <th className="col-amount">Amount</th>
                </tr>
              </thead>
              <tbody>
                {items.map((item, index) => (
                  <tr key={index}>
                    {/* For now, 'Ordered' might correspond to Quantity or Weight? Using Weight for now */}
                    <td><input type="text" value={item.weight} onChange={(e) => handleItemChange(index, 'weight', e.target.value)} placeholder="Weight/Type" /></td>
                    <td><input type="text" value={item.description} onChange={(e) => handleItemChange(index, 'description', e.target.value)} placeholder="Item Description" /></td>
                    <td><input type="text" value={invoiceDate} readOnly disabled className="item-date"/></td> {/* Maybe fixed date? */}
                    <td><input type="number" value={item.quantity} onChange={(e) => handleItemChange(index, 'quantity', e.target.value)} placeholder="0" /></td>
                    <td><input type="number" step="0.01" value={item.price} onChange={(e) => handleItemChange(index, 'price', e.target.value)} placeholder="0.00" /></td>
                    <td className="amount-cell">${item.amount.toFixed(2)}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          {/* Invoice Footer - Total */}
          <div className="invoice-footer">
             <div className="total-section">
                <span>TOTAL</span>
                <span className="total-value">${totalAmount.toFixed(2)}</span>
            </div>
          </div>
        </div>
      </div>
    </ManagerLayout>
  );
}

export default ManagerNewInvoice;
